import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, throwError, of } from 'rxjs';
import { catchError, map, expand, takeWhile, toArray } from 'rxjs/operators';
import { Server, ApiResponse } from '\models\battlemetrics.models'; // Update the path as necessary

@Injectable({
  providedIn: 'root'
})
export class DataService {
  private apiUrl = 'https://api.battlemetrics.com/servers';
  private game = 'rust';
  private token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbiI6ImJlYWRmMWNmMTNiZmM3NmYiLCJpYXQiOjE3MTc1MjUzNDQsIm5iOiIY2NzUyNTM0NCwiaXNzIjoiaHR0cHM6Ly93d3cuYmF0dGxlbWV0cmljcy5jb20iLCJzdWIiOiJ1cm46dXNlcjo4NjgxMDgifQ.2pYP4c7L8AZO5y9-ZrCQK1CwqqoWTdCkusJduZbLFOg';

  constructor(private http: HttpClient) {}

  getData(): Observable<Server[]> {
    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.token}`);

    return this.fetchPage(1, headers).pipe(
      expand(response => {
        if (response && response.links && response.links.next) {
          const nextPageUrl = response.links.next;
          return this.http.get<ApiResponse>(nextPageUrl, { headers });
        } else {
          return of(null);
        }
      }),
      takeWhile(response => response !== null),
      map(response => response!.data),
      toArray(),
      map(pages => pages.flat()),
      catchError(error => {
        console.error('Error fetching data:', error);
        return throwError(error);
      })
    );
  }

  private fetchPage(page: number, headers: HttpHeaders): Observable<ApiResponse> {
    const url = `${this.apiUrl}?filter[game]=${encodeURIComponent(this.game)}&page[size]=100&page[number]=${page}`;
    console.log('Request URL:', url); // Log the request URL for debugging

    return this.http.get<ApiResponse>(url, { headers }).pipe(
      map(response => {
        console.log('API Response:', response); // Log the response for debugging
        if (response && response.data) {
          return response;
        } else {
          console.warn('Empty or malformed response:', response);
          return { data: [] };
        }
      }),
      catchError(error => {
        console.error('Error fetching page data:', error);
        return throwError(error);
      })
    );
  }
}
