// src/app/data.service.ts
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, map, expand, reduce } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class DataService {
  private apiUrl = 'https://api.battlemetrics.com/servers';
  private game = 'palworld';
  private token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbiI6ImJlYWRmMWNmMTNiZmM3NmYiLCJpYXQiOjE3MTc1MjUzNDQsIm5iZiI6MTcxNzUyNTM0NCwiaXNzIjoiaHR0cHM6Ly93d3cuYmF0dGxlbWV0cmljcy5jb20iLCJzdWIiOiJ1cm46dXNlcjo4NjgxMDgifQ.2pYP4c7L8AZO5y9-ZrCQK1CwqqoWTdCkusJduZbLFOg';

  constructor(private http: HttpClient) {}

  getData(): Observable<any[]> {
    return this.fetchServers(1, 100).pipe(
      reduce<any[], any[]>((acc, curr) => acc.concat(curr), [])
    );
  }

  private fetchServers(page: number, size: number): Observable<any[]> {
    const url = `${this.apiUrl}?filter[game]=${encodeURIComponent(this.game)}&page[size]=${size}&page[number]=${page}`;
    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.token}`);

    console.log('Requesting URL:', url); // Log the request URL for debugging

    return this.http.get<any>(url, { headers }).pipe(
      map(response => {
        console.log('API Response:', response); // Log the response for debugging
        if (response && response.data) {
          return response.data;
        } else {
          console.warn('Empty or malformed response:', response);
          return [];
        }
      }),
      expand((data) => {
        console.log('Data length:', data.length); // Log the length of the data array
        return data.length === size ? this.fetchServers(page + 1, size) : [];
      }),
      catchError(error => {
        console.error('Error fetching data:', error);
        return throwError(error);
      })
    );
  }
}
